#!/bin/bash\n\n# Shadow Report Alliance - Environment Setup Script\n\necho "Setting up environment files..."\n\n# Frontend .env\nif [ ! -f .env ]; then\n  cp .env.example .env\n  echo "‚úÖ Created .env from .env.example"\nelse\n  echo "‚ö†Ô∏è  .env already exists, skipping"\nfi\n\n# Server .env\nif [ ! -f server/.env ]; then\n  cp server/.env.example server/.env\n  # Generate JWT secret\n  JWT_SECRET=$(openssl rand -base64 32)\n  # Update JWT secret in server/.env\n  if [[ "$OSTYPE" == "darwin"* ]]; then\n    sed -i "" "s/your_jwt_secret_key_here/$JWT_SECRET/g" server/.env\n  else\n    sed -i "s/your_jwt_secret_key_here/$JWT_SECRET/g" server/.env\n  fi\n  echo "‚úÖ Created server/.env with generated JWT secret"\nelse\n  echo "‚ö†Ô∏è  server/.env already exists, skipping"\nfi\n\necho "\nüìã Next steps:"\necho "1. Edit .env and add your Supabase credentials"\necho "2. Review server/.env settings"\necho "3. Run npm install && cd server && npm install"\necho "4. Start the development servers"
